public class exe03 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n = 23, m = 78;
		System.out.println((int)(n + m) + "  " + (n - m) + "  " + n * m + "  " + n
				% m);
		float l = 23f, g = 78f;

		System.out.println((l + g) + "  " + (l - g) + "  " + (l * g) + "  "
				+ (l % g));
	}
}

